import React, { useState } from "react";
import Form from '../styles/form'
import Axios from 'axios'
const baseUrl = 'http://localhost:8080'

export default function SingUp () {
    const [form, setForm] = useState({
        email: 'Seu email aqui',
        password: 'Sua senha aqui'
    })

    const getValues = (e) => {
        if (typeof(e.value) === 'string' && e.value !== null) {
            setForm({...form, [e.name]: e.value})
        }

        e.classList.add('on')
    }

    const getUser = () => {
        Axios.get(`${baseUrl}/user/${form.email}/${form.password}`).then(res => {console.log(res.data)})
    }

    return (
    <Form>
        <h2>Sing up</h2>

        <div>
            <label>
                E-mail:

                <input type='text' name='email' value={form.email} onChange={(e) => {getValues(e)}} />
            </label>

            <label>
                Senha:

                <input type='text' name='password' value={form.password} onChange={(e) => {getValues(e)}} />
            </label>
        </div>

        <button onClick={() => {getUser()}}>Criar conta</button>
    </Form>
    )
}
